import UIKit

enum Icon {
    case bankID
    case password
    case profile

    fileprivate var base64Encoded: String {
        switch self {
        case .bankID:
            return ""
        case .password:
            return ""
        case .profile:
            return ""
        }
    }
}

extension UIImage {
    convenience init?(icon: Icon) {
        guard let data = Data(base64Encoded: icon.base64Encoded)
            else { return nil }
        self.init(data: data)
    }
}
