import UIKit

enum Icon {
    case bankID
    case password

    private var base64Encoded: String {
        switch self {
        case .bankID:
            return ""
        case .password:
            return ""
        }
    }

    func makeImage() -> UIImage? {
        guard let data = Data(base64Encoded: base64Encoded),
            let image = UIImage(data: data)
            else { return nil }
        return image.withRenderingMode(.alwaysTemplate)
    }
}
